import { TemplateManager } from '../../templates/TemplateManager.js';
import { TemplateEngine } from '../../templates/TemplateEngine.js';
import { HandlebarsTemplateEngine } from '../../templates/HandlebarsTemplateEngine.js';
import { EjsTemplateEngine } from '../../templates/EjsTemplateEngine.js';
import { createMockParsedContent } from '../utils/test-helpers.js';
import * as _fs from 'fs';
import * as path from 'path';

// Mock dependencies
jest.mock('fs');
jest.mock('path');
jest.mock('../../templates/HandlebarsTemplateEngine');
jest.mock('../../templates/EjsTemplateEngine');

describe('TemplateManager', () => {
  let templateManager: TemplateManager;
  let mockHandlebarsEngine: jest.Mocked<HandlebarsTemplateEngine>;
  let mockEjsEngine: jest.Mocked<EjsTemplateEngine>;

  beforeEach(() => {
    // Reset mocks
    jest.clearAllMocks();

    // Create mock template engines
    mockHandlebarsEngine = new HandlebarsTemplateEngine() as jest.Mocked<HandlebarsTemplateEngine>;
    mockEjsEngine = new EjsTemplateEngine() as jest.Mocked<EjsTemplateEngine>;

    // Mock the constructors
    (HandlebarsTemplateEngine as jest.Mock).mockImplementation(() => mockHandlebarsEngine);
    (EjsTemplateEngine as jest.Mock).mockImplementation(() => mockEjsEngine);

    // Create the template manager
    templateManager = new TemplateManager({
      defaultEngine: 'handlebars',
      handlebarsOptions: {
        partialsDir: '/test/partials',
      },
      ejsOptions: {
        includesDir: '/test/includes',
      },
    });
  });

  test('should initialize with default engines', () => {
    expect(templateManager).toBeDefined();
    expect(HandlebarsTemplateEngine).toHaveBeenCalled();
    expect(EjsTemplateEngine).toHaveBeenCalled();
  });

  test('should register a custom template engine', () => {
    // Create a mock custom engine
    const mockCustomEngine = {
      render: jest.fn().mockResolvedValue('<div>Custom Template</div>'),
      renderContent: jest.fn().mockResolvedValue('<div>Custom Content</div>'),
      clearCache: jest.fn(),
    } as unknown as TemplateEngine;

    // Register the custom engine
    templateManager.registerEngine('custom', mockCustomEngine);

    // Get the custom engine
    const engine = templateManager.getEngine('custom');
    expect(engine).toBe(mockCustomEngine);
  });

  test('should get the default engine when no name is provided', () => {
    const engine = templateManager.getEngine();
    expect(engine).toBe(mockHandlebarsEngine);
  });

  test('should throw an error when getting a non-existent engine', () => {
    expect(() => templateManager.getEngine('non-existent')).toThrow();
  });

  test('should infer engine from file extension', async () => {
    // Mock path.extname to return the file extension
    (path.extname as jest.Mock).mockImplementation((filePath: string) => {
      const parts = filePath.split('.');
      return parts.length > 1 ? `.${parts[parts.length - 1]}` : '';
    });

    // Mock the render methods
    mockHandlebarsEngine.render.mockResolvedValue('<div>Handlebars Template</div>');
    mockEjsEngine.render.mockResolvedValue('<div>EJS Template</div>');

    // Render templates with different extensions
    await templateManager.render('template.hbs', { title: 'Test' });
    await templateManager.render('template.ejs', { title: 'Test' });
    await templateManager.render('template.txt', { title: 'Test' });

    // Verify that the correct engines were used
    expect(mockHandlebarsEngine.render).toHaveBeenCalledTimes(2); // .hbs and .txt (default)
    expect(mockEjsEngine.render).toHaveBeenCalledTimes(1); // .ejs
  });

  test('should render content with the specified engine', async () => {
    // Create mock parsed content
    const mockContent = createMockParsedContent({
      title: 'Test Document',
      description: 'Test description',
    });

    // Mock the renderContent method
    mockHandlebarsEngine.renderContent.mockResolvedValue('<div>Rendered Content</div>');

    // Render content with the handlebars engine
    const result = await templateManager.renderContent(
      mockContent,
      'template.hbs',
      undefined,
      'handlebars'
    );

    // Verify the result
    expect(result).toBe('<div>Rendered Content</div>');
    expect(mockHandlebarsEngine.renderContent).toHaveBeenCalledWith(
      mockContent,
      'template.hbs',
      undefined
    );
  });

  test('should clear cache for all engines', () => {
    // Clear the cache
    templateManager.clearCache();

    // Verify that clearCache was called on all engines
    expect(mockHandlebarsEngine.clearCache).toHaveBeenCalled();
    expect(mockEjsEngine.clearCache).toHaveBeenCalled();
  });
});

import { PluginDocsGenerator } from '../../tools/plugin-docs-generator.js';
import { Plugin } from '../../../types/plugin.js';
import * as fs from 'fs';
import * as path from 'path';

// Mock dependencies
jest.mock('fs');
jest.mock('path');

describe('PluginDocsGenerator', () => {
  let generator: PluginDocsGenerator;
  let mockPlugin1: Plugin;
  let mockPlugin2: Plugin;

  beforeEach(() => {
    // Reset mocks
    jest.clearAllMocks();

    // Mock fs.existsSync to return false for directories
    (fs.existsSync as jest.Mock).mockReturnValue(false);

    // Mock fs.mkdirSync to do nothing
    (fs.mkdirSync as jest.Mock).mockImplementation(() => {
      return undefined;
    });

    // Define interface for fs.promises mock to replace 'any'
    interface FSPromisesMock {
      writeFile: jest.Mock;
      [key: string]: unknown; // Allow for additional properties
    }

    // Define interface for PluginDocsGenerator internals to avoid using 'any'
    interface _PluginDocsGeneratorInternals {
      plugins: Plugin[];
      [key: string]: unknown; // Allow for additional properties
    }

    // Mock fs.promises.writeFile to do nothing
    (fs.promises as unknown as FSPromisesMock) = {
      writeFile: jest.fn().mockResolvedValue(undefined),
    };

    // Mock path.join to concatenate paths
    (path.join as jest.Mock).mockImplementation((...paths: string[]) => paths.join('/'));

    // Create mock plugins
    mockPlugin1 = {
      name: 'test-plugin-1',
      description: 'A test plugin',
      hooks: {
        beforeParse: jest.fn(),
        afterParse: jest.fn(),
      },
      options: {
        option1: {
          type: 'string',
          description: 'Option 1 description',
          required: true,
        },
        option2: {
          type: 'boolean',
          description: 'Option 2 description',
          required: false,
          default: false,
        },
      },
    };

    mockPlugin2 = {
      name: 'test-plugin-2',
      description: 'Another test plugin',
      hooks: {
        beforeBuild: jest.fn(),
        afterBuild: jest.fn(),
      },
    };

    // Create the generator
    generator = new PluginDocsGenerator('/test/docs');
  });

  test('should initialize with the output directory', () => {
    expect(generator).toBeDefined();
  });

  test('should add plugins', () => {
    // Add plugins
    generator.addPlugin(mockPlugin1);
    generator.addPlugin(mockPlugin2);

    // Verify that the plugins were added
    expect((generator as unknown as PluginDocsGeneratorInternals).plugins).toHaveLength(2);
    expect((generator as unknown as PluginDocsGeneratorInternals).plugins[0]).toBe(mockPlugin1);
    expect((generator as unknown as PluginDocsGeneratorInternals).plugins[1]).toBe(mockPlugin2);
  });

  test('should generate documentation', async () => {
    // Add plugins
    generator.addPlugin(mockPlugin1);
    generator.addPlugin(mockPlugin2);

    // Generate documentation
    await generator.generateDocs();

    // Verify that the output directory was created
    expect(fs.existsSync).toHaveBeenCalledWith('/test/docs');
    expect(fs.mkdirSync).toHaveBeenCalledWith('/test/docs', { recursive: true });

    // Verify that the index file was generated
    expect(fs.promises.writeFile).toHaveBeenCalledWith(
      '/test/docs/README.md',
      expect.stringContaining('# Plugin Documentation'),
      undefined
    );

    // Verify that the plugin documentation files were generated
    expect(fs.promises.writeFile).toHaveBeenCalledWith(
      '/test/docs/test-plugin-1.md',
      expect.stringContaining('# test-plugin-1 Plugin'),
      undefined
    );
    expect(fs.promises.writeFile).toHaveBeenCalledWith(
      '/test/docs/test-plugin-2.md',
      expect.stringContaining('# test-plugin-2 Plugin'),
      undefined
    );
  });

  test('should generate index file with plugin links', async () => {
    // Add plugins
    generator.addPlugin(mockPlugin1);
    generator.addPlugin(mockPlugin2);

    // Generate documentation
    await generator.generateDocs();

    // Get the index file content
    const indexFileCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/README.md'
    );
    const indexContent = indexFileCall ? indexFileCall[1] : '';

    // Verify the index content
    expect(indexContent).toContain('# Plugin Documentation');
    expect(indexContent).toContain('## Available Plugins');
    expect(indexContent).toContain('- [test-plugin-1](test-plugin-1.md): A test plugin');
    expect(indexContent).toContain('- [test-plugin-2](test-plugin-2.md): Another test plugin');
  });

  test('should generate plugin documentation with hooks', async () => {
    // Add a plugin
    generator.addPlugin(mockPlugin1);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/test-plugin-1.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('# test-plugin-1 Plugin');
    expect(pluginDocContent).toContain('A test plugin');
    expect(pluginDocContent).toContain('## Hooks');
    expect(pluginDocContent).toContain('- `beforeParse`: Called before parsing content');
    expect(pluginDocContent).toContain('- `afterParse`: Called after parsing content');
  });

  test('should generate plugin documentation with options', async () => {
    // Add a plugin
    generator.addPlugin(mockPlugin1);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/test-plugin-1.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('## Options');
    expect(pluginDocContent).toContain('| Option | Type | Description | Required | Default |');
    expect(pluginDocContent).toContain('| `option1` | `string` | Option 1 description | Yes | - |');
    expect(pluginDocContent).toContain(
      '| `option2` | `boolean` | Option 2 description | No | false |'
    );
  });

  test('should handle plugins without hooks', async () => {
    // Create a plugin without hooks
    const pluginWithoutHooks: Plugin = {
      name: 'plugin-without-hooks',
      description: 'A plugin without hooks',
    };

    // Add the plugin
    generator.addPlugin(pluginWithoutHooks);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/plugin-without-hooks.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('## Hooks');
    expect(pluginDocContent).toContain('This plugin does not implement any hooks.');
  });

  test('should handle plugins without options', async () => {
    // Create a plugin without options
    const pluginWithoutOptions: Plugin = {
      name: 'plugin-without-options',
      description: 'A plugin without options',
      hooks: {
        beforeParse: jest.fn(),
      },
    };

    // Add the plugin
    generator.addPlugin(pluginWithoutOptions);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/plugin-without-options.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('## Options');
    expect(pluginDocContent).toContain('This plugin does not have any options.');
  });

  test('should handle plugins without description', async () => {
    // Create a plugin without description
    const pluginWithoutDescription: Plugin = {
      name: 'plugin-without-description',
      hooks: {
        beforeParse: jest.fn(),
      },
    };

    // Add the plugin
    generator.addPlugin(pluginWithoutDescription);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/plugin-without-description.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('# plugin-without-description Plugin');
    expect(pluginDocContent).toContain('No description available.');
  });

  test('should handle plugins with examples', async () => {
    // Create a plugin with examples
    const pluginWithExamples: Plugin = {
      name: 'plugin-with-examples',
      description: 'A plugin with examples',
      hooks: {
        beforeParse: jest.fn(),
      },
      examples: [
        'const plugin = new ExamplePlugin();\ngenerator.addPlugin(plugin);',
        'const plugin = new ExamplePlugin({ option: "value" });\ngenerator.addPlugin(plugin);',
      ],
    };

    // Add the plugin
    generator.addPlugin(pluginWithExamples);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/plugin-with-examples.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('## Examples');
    expect(pluginDocContent).toContain('```javascript');
    expect(pluginDocContent).toContain('const plugin = new ExamplePlugin();');
    expect(pluginDocContent).toContain('const plugin = new ExamplePlugin({ option: "value" });');
  });

  test('should handle plugins without examples', async () => {
    // Add a plugin without examples
    generator.addPlugin(mockPlugin1);

    // Generate documentation
    await generator.generateDocs();

    // Get the plugin documentation content
    const pluginDocCall = (fs.promises.writeFile as jest.Mock).mock.calls.find(
      call => call[0] === '/test/docs/test-plugin-1.md'
    );
    const pluginDocContent = pluginDocCall ? pluginDocCall[1] : '';

    // Verify the plugin documentation content
    expect(pluginDocContent).toContain('## Examples');
    expect(pluginDocContent).toContain('No examples available.');
  });
});

import { Plugin } from '../../types/plugin.js';
import { ParsedContent } from '../../types/parser.js';
import { logger } from '../utils/logger.js';

export class PluginManager {
  private plugins: Plugin[] = [];
  // Flag to track if the plugin list has been modified

  constructor(plugins: Plugin[] = []) {
    this.plugins = [...plugins];
  }

  /**
   * Get all registered plugins
   */
  getPlugins(): Plugin[] {
    return this.plugins;
  }

  /**
   * Register a new plugin
   */
  registerPlugin(plugin: Plugin): void {
    this.plugins.push(plugin);
  }

  /**
   * Initialize all plugins that have an initialize method
   */
  async initializePlugins(): Promise<void> {
    for (const plugin of this.plugins) {
      if (plugin.initialize) {
        await plugin.initialize();
      }
    }
  }

  /**
   * Execute a specific hook for all plugins
   */
  executeHook<T>(hookName: string, data: T, continueOnError = false): T {
    let result = data;

    for (const plugin of this.plugins) {
      if (plugin.hooks && plugin.hooks[hookName]) {
        try {
          result = plugin.hooks[hookName](result, plugin.options);
        } catch (error) {
          if (!continueOnError) {
            throw error;
          }
          logger.warn(`Error executing hook ${hookName} for plugin ${plugin.name}: ${error}`);
        }
      }
    }

    return result;
  }

  /**
   * Execute a specific hook for a specific plugin
   */
  executeHookForPlugin<T>(hookName: string, pluginName: string, data: T): T {
    const plugin = this.getPluginByName(pluginName);

    if (!plugin || !plugin.hooks || !plugin.hooks[hookName]) {
      return data;
    }

    try {
      return plugin.hooks[hookName](data, plugin.options);
    } catch (error) {
      logger.error(`Error executing hook ${hookName} for plugin ${pluginName}: ${error}`);
      throw error;
    }
  }

  /**
   * Get a plugin by name
   */
  getPluginByName(name: string): Plugin | undefined {
    return this.plugins.find(plugin => plugin.name === name);
  }

  /**
   * Check if a plugin exists
   */
  hasPlugin(name: string): boolean {
    return this.plugins.some(plugin => plugin.name === name);
  }

  /**
   * Remove a plugin by name
   */
  removePlugin(name: string): void {
    const index = this.plugins.findIndex(plugin => plugin.name === name);
    if (index !== -1) {
      this.plugins.splice(index, 1);
    }
  }

  /**
   * Legacy methods for backward compatibility
   */
  async applyBeforeParse(content: string, filePath: string): Promise<string> {
    return this.executeHook('beforeParse', content, filePath);
  }

  async applyAfterParse(parsedContent: any, filePath: string): Promise<any> {
    return this.executeHook('afterParse', parsedContent, filePath);
  }

  getPlugin(name: string): Plugin | undefined {
    return this.getPluginByName(name);
  }

  register(plugin: Plugin): void {
    this.registerPlugin(plugin);
  }
}

export interface ThemeStyles {
  stylesheet: string;
  variables: Record<string, string>;
  additionalCSS?: string;
}

export class DocsifyThemeAdapter {
  private theme: string;

  constructor(theme = 'vue') {
    this.theme = theme.toLowerCase();
  }

  getThemeStyles(): ThemeStyles {
    switch (this.theme) {
      case 'vue':
        return this.getVueTheme();
      case 'dark':
        return this.getDarkTheme();
      case 'buble':
        return this.getBubleTheme();
      case 'pure':
        return this.getPureTheme();
      case 'dolphin':
        return this.getDolphinTheme();
      default:
        return this.getVueTheme();
    }
  }

  private getVueTheme(): ThemeStyles {
    return {
      stylesheet: 'https://cdn.jsdelivr.net/npm/docsify@4/lib/themes/vue.css',
      variables: {
        '--theme-color': '#42b983',
        '--theme-color-dark': '#33a06f',
        '--text-color-base': '#2c3e50',
        '--text-color-secondary': '#646473',
        '--text-color-tertiary': '#81818e'
      }
    };
  }

  private getDarkTheme(): ThemeStyles {
    return {
      stylesheet: 'https://cdn.jsdelivr.net/npm/docsify@4/lib/themes/dark.css',
      variables: {
        '--theme-color': '#ea6f5a',
        '--theme-color-dark': '#c85849',
        '--text-color-base': '#b4b4b4',
        '--text-color-secondary': '#efefef',
        '--text-color-tertiary': '#eee',
        '--background-color': '#252529'
      }
    };
  }

  private getBubleTheme(): ThemeStyles {
    return {
      stylesheet: 'https://cdn.jsdelivr.net/npm/docsify@4/lib/themes/buble.css',
      variables: {
        '--theme-color': '#0074d9',
        '--theme-color-dark': '#0059a6',
        '--text-color-base': '#333',
        '--text-color-secondary': '#555',
        '--text-color-tertiary': '#777'
      }
    };
  }

  private getPureTheme(): ThemeStyles {
    return {
      stylesheet: 'https://cdn.jsdelivr.net/npm/docsify@4/lib/themes/pure.css',
      variables: {
        '--theme-color': '#0074d9',
        '--theme-color-dark': '#0059a6',
        '--text-color-base': '#333',
        '--text-color-secondary': '#555',
        '--text-color-tertiary': '#777'
      }
    };
  }

  private getDolphinTheme(): ThemeStyles {
    return {
      stylesheet: 'https://cdn.jsdelivr.net/npm/docsify-themeable@0/dist/css/theme-simple.css',
      variables: {
        '--theme-color': '#1E90FF',
        '--theme-color-dark': '#0077ea',
        '--text-color-base': '#333',
        '--text-color-secondary': '#555',
        '--text-color-tertiary': '#777',
        '--sidebar-background': '#f8f8f8',
        '--sidebar-nav-link-color': '#444',
        '--sidebar-nav-link-color--active': '#1E90FF',
        '--sidebar-name-color': '#1E90FF'
      },
      additionalCSS: `
        .sidebar {
          border-right: 1px solid rgba(0,0,0,0.07);
        }
        .markdown-section code {
          border-radius: 3px;
          background-color: #f8f8f8;
          color: #e96900;
        }
      `
    };
  }

  generateThemeCSS(): string {
    const { variables, additionalCSS } = this.getThemeStyles();
    let css = ':root {\n';

    for (const [key, value] of Object.entries(variables)) {
      css += `  ${key}: ${value};\n`;
    }

    css += '}\n';

    if (additionalCSS) {
      css += additionalCSS;
    }

    return css;
  }
}

import { logger } from './logger.js';

/**
 * Base error class for all site generator errors
 * Provides standardized error handling with error codes and contextual information
 */
export class SiteGeneratorError extends Error {
  code: string;
  context?: Record<string, unknown>;

  constructor(message: string, code: string, context?: Record<string, unknown>) {
    super(message);
    this.name = this.constructor.name;
    this.code = code;
    this.context = context;

    // Capture stack trace
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor);
    }
  }

  /**
   * Returns a formatted error message with context information
   */
  getFormattedMessage(): string {
    let message = `[${this.code}] ${this.message}`;

    if (this.context && Object.keys(this.context).length > 0) {
      message += '\nContext:';
      for (const [key, value] of Object.entries(this.context)) {
        message += `\n  ${key}: ${JSON.stringify(value)}`;
      }
    }

    return message;
  }
}

